mapper.select:
select

from user


   AND user_id = #{userId,jdbcType=VARCHAR}


    AND name = #{name,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND time >= #{beginDate}


    AND time <=#{endDate}

mapper.delete:
delete from user


    AND user_id = #{userId,jdbcType=VARCHAR}


    AND name = #{name,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}
